import{_ as o,c as s,a2 as r,o as l}from"./chunks/framework.De1yK2Gx.js";const i="/help/assets/pro_vimo_1.qaTcHPex.png",n="/help/assets/pro_vimo_2.DyralHwN.png",a="/help/assets/pro_vimo_3.V7kljR56.png",e="/help/assets/pro_vimo_4.BmtzOswd.png",f=JSON.parse('{"title":"多种任务展示模式","description":"","frontmatter":{},"headers":[],"relativePath":"functions/projects/view-modes.md","filePath":"zh/functions/projects/view-modes.md"}'),p={name:"functions/projects/view-modes.md"};function g(c,t,u,h,d,m){return l(),s("div",null,t[0]||(t[0]=[r('<h1 id="多种任务展示模式" tabindex="-1">多种任务展示模式 <a class="header-anchor" href="#多种任务展示模式" aria-label="Permalink to &quot;多种任务展示模式&quot;">​</a></h1><p>系统支持多种任务展示模式，适应不同的管理需求。</p><h2 id="列表视图" tabindex="-1">列表视图 <a class="header-anchor" href="#列表视图" aria-label="Permalink to &quot;列表视图&quot;">​</a></h2><p><strong>功能与特点</strong>：</p><ul><li><strong>直观分类</strong>：按照我的任务、协助的任务、未完成任务、已完成任务展示可折叠的任务列表。</li><li><strong>结构化展示</strong>：以表格形式列出所有任务，字段包括任务名称、列表、优先级、负责人、到期时间。</li><li><strong>排序和过滤</strong>：支持按优先级或到期时间排序，提供条件过滤功能。</li></ul><p><strong>适用场景</strong>：</p><ul><li>大量任务需要逐项跟进时。</li><li>强调任务细节及分类归档。</li></ul><p><img src="'+i+'" alt="列表视图"></p><h2 id="看板视图" tabindex="-1">看板视图 <a class="header-anchor" href="#看板视图" aria-label="Permalink to &quot;看板视图&quot;">​</a></h2><p><strong>功能与特点</strong>：</p><ul><li><strong>任务卡片化</strong>：项目中的任务以卡片形式展示，每张卡片包含标题、负责人、进度条等信息。</li><li><strong>列表示状态</strong>：每列对应每个项目中的各个任务。</li><li><strong>拖拽操作</strong>：通过拖拽任务卡片进行任务重新排序。</li><li><strong>标签标记</strong>：左侧卡片有颜色标记，进度条右侧也有对应倒计时标签标记，用于展示优先级。</li></ul><p><strong>适用场景</strong>：</p><ul><li>团队协作时，用于直观展示任务进度。</li><li>关注任务的整体流转情况。</li></ul><p><img src="'+n+'" alt="看板视图"></p><h2 id="时间线视图" tabindex="-1">时间线视图 <a class="header-anchor" href="#时间线视图" aria-label="Permalink to &quot;时间线视图&quot;">​</a></h2><p><strong>功能与特点</strong>：</p><ul><li><strong>任务时间轴展示</strong>：任务按照时间跨度以甘特图形式排列，横轴为时间，纵轴为任务。</li><li><strong>拖拽操作</strong>：拖动甘特图上的任务条块调整起止时间。</li></ul><p><strong>适用场景</strong>：</p><ul><li>项目计划制定和任务排期。</li><li>需要掌握任务时间安排。</li></ul><p><img src="'+a+'" alt="时间线视图"></p><h2 id="日历视图" tabindex="-1">日历视图 <a class="header-anchor" href="#日历视图" aria-label="Permalink to &quot;日历视图&quot;">​</a></h2><p><strong>功能与特点</strong>：</p><ul><li><strong>日历布局</strong>：以日、周或月为单位展示任务。</li><li><strong>时间标记</strong>：任务根据截止日期、起止时间显示在日历的对应时间块中。</li><li><strong>任务概览</strong>：点击任务可查看详细信息或删除。</li><li><strong>拖拽操作</strong>：拖动日历上的任务条块调整起止时间。</li></ul><p><strong>适用场景</strong>：</p><ul><li>日常工作安排和短期规划。</li><li>快速了解每日或每周的工作重点。</li></ul><p><img src="'+e+'" alt="日历视图"></p>',26)]))}const q=o(p,[["render",g]]);export{f as __pageData,q as default};
