import{_ as e,c as t,a2 as r,o as s}from"./chunks/framework.BQmytedh.js";const n="/assets/remind_1.DknTXDPS.png",i="/assets/remind_2.C1-y6i4J.png",o="/assets/remind_3.Bbow4CVO.png",p="/assets/remind_4.4Yn9nRX8.png",c="/assets/remind_5.BsmS_cDh.png",q=JSON.parse('{"title":"提醒功能","description":"","frontmatter":{},"headers":[],"relativePath":"functions/reminders.md","filePath":"zh/functions/reminders.md"}'),d={name:"functions/reminders.md"};function l(h,a,m,_,u,f){return s(),t("div",null,a[0]||(a[0]=[r('<h1 id="提醒功能" tabindex="-1">提醒功能 <a class="header-anchor" href="#提醒功能" aria-label="Permalink to &quot;提醒功能&quot;">​</a></h1><p>提醒功能是为了帮助用户在工作中保持高效，确保任务按时完成，并及时获知任务状态和审批进度。系统通过多种提醒方式，如任务截止提醒、实时状态更新提醒和签到提醒，帮助用户和团队成员更好地管理时间、提高工作效率，并加强协作。</p><h2 id="新任务提醒" tabindex="-1">新任务提醒 <a class="header-anchor" href="#新任务提醒" aria-label="Permalink to &quot;新任务提醒&quot;">​</a></h2><p><img src="'+n+'" alt="新任务提醒"></p><p>系统根据任务的创建时间和设定的提醒规则，自动发送新任务提醒，确保用户不会错过新任务的开始。这种提醒方式帮助用户及时了解新任务，并为任务的顺利进行提供了明确的时间预警。</p><h2 id="任务到期提醒" tabindex="-1">任务到期提醒 <a class="header-anchor" href="#任务到期提醒" aria-label="Permalink to &quot;任务到期提醒&quot;">​</a></h2><p><img src="'+i+'" alt="任务到期提醒"></p><p>系统会根据任务的截止时间，提前自动发送到期提醒，确保用户按时完成任务。通过合理的时间管理，用户可以有效避免任务延误，提升工作效率。</p><h2 id="任务实时更新提醒" tabindex="-1">任务实时更新提醒 <a class="header-anchor" href="#任务实时更新提醒" aria-label="Permalink to &quot;任务实时更新提醒&quot;">​</a></h2><p><img src="'+o+'" alt="任务实时更新提醒"></p><p>当任务的状态发生变化（例如任务被分配给其他成员、任务的截止时间被修改或任务完成等），系统会实时发送通知。该提醒功能确保任务相关人员第一时间了解任务动态，从而帮助团队保持高效协作和信息同步。</p><h2 id="签到时间提醒" tabindex="-1">签到时间提醒 <a class="header-anchor" href="#签到时间提醒" aria-label="Permalink to &quot;签到时间提醒&quot;">​</a></h2><p><img src="'+p+'" alt="签到时间提醒"></p><p>系统会根据设定的签到时间提前提醒用户，确保团队成员按时签到并保持规律的工作习惯。这种提醒有助于提升团队的工作效率和纪律性。</p><h2 id="审批情况提醒" tabindex="-1">审批情况提醒 <a class="header-anchor" href="#审批情况提醒" aria-label="Permalink to &quot;审批情况提醒&quot;">​</a></h2><p><img src="'+c+'" alt="审批情况提醒"></p><p>针对任务的审批流程（如请假、加班或其他审批事项），系统会自动提醒审批人处理待办事项。同时，当审批完成后，会通知相关人员审批结果，确保流程的顺畅和及时反馈。</p><hr><p>通过这些灵活的提醒功能，用户能够在繁忙的工作中更好地管理时间、跟进任务进度和审批事项，避免遗漏重要的工作内容，从而提升整体工作效率和团队协作的流畅度。</p>',19)]))}const g=e(d,[["render",l]]);export{q as __pageData,g as default};
